import { Component } from "react";

export default class Event extends Component {
    constructor(props) {
        super(props);

        if (props.model) {
            this.state = props.model;
        }
    }

    componentDidMount() {
        if (!this.state) {
            this.setState({
                event: {
                    name: "event name",
                    orgID: 2,
                    description: "event description",
                    startTime: new Date(),
                    endTime: new Date(),
                    tags: ["gaming", "greek"],
                    location: "price center",
                    created: new Date(),
                    updated: new Date(),
                },
                org: {
                    name: "org name",
                },
            })
        }
    }

    previewSquare() {

    }

    previewWide() {
        console.log(this.state);
        return (
            <div>
                <h2><a href={"/events/${this.state.event.id}"}>{this.state.event.name}</a></h2>
                <table>
                    <tr>
                        <tc>By: </tc>
                        <tc>{this.state.org.name}</tc>
                    </tr>
                    <tr>
                        <tc>On: </tc>
                        <tc>{this.state.event.startTime.toISOString()}</tc>
                    </tr>

                </table>
            </div>
        );
    }

    render() {
        if (!this.state) {
            return <div/>;
        }

        switch (this.props.preview) {
        case "square":
            return this.previewSquare()
        case "wide":
            return this.previewWide()
        }

        return (
            <div>
                <h1>{this.state.name}</h1>
            </div>
        );
    }
}
