import { Component } from "react";
import { Link } from "react-router-dom";
import libuser from "../lib/user";

class Trending extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <div/>
    }
}

class EventSideScroll extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <div/>
    }
}

class WelcomeBanner extends Component {
    constructor(props) {
        super(props)
    }

    render() {
        return <div/>
    }
}



export default class Home extends Component {
    constructor(props) {
        super(props)
    }

    componentDidMount() {
        this.setState({
            user: libuser.getUser(),
        });
    }

    userFavorites() {
        return (
            <>
                <h2>Favorited Events</h2>
                <EventSideScroll events={[]} />

                <h2>Upcoming events from favorited Orgs and Tags</h2>
                <EventSideScroll events={[]} />
            </>
        );
    }


    render() {
        let top;
        if (user && (user.eventFavorites || user.tagFavorites || user.orgFavorites)) {
            top = this.userFavorites();
        } else {
            top = (<WelcomeBanner/>);
        }

        return (
            <>
                {top}
                <Trending/>
            </>
        );
    }
}
