import { Fragment, Component } from "react";
import { Link } from "react-router-dom";

export default class Event extends Component {
    constructor(props) {
        super(props);

        if (props.model) {
            this.state = props.model;
        }
    }

    componentDidMount() {
        if (!this.state) {
            this.setState({
                event: {
                    id: this.props.eventID,
                    name: "event name",
                    orgID: 2,
                    description: "event description",
                    startTime: new Date(),
                    endTime: new Date(),
                    tags: ["gaming", "greek"],
                    location: "price center",
                    created: new Date(),
                    updated: new Date(),
                },
                org: {
                    name: "org name",
                },
            });
        }
    }

    preview(className) {
        return (
            <>
                <h2>
                    <Link to={`/events/${this.state.event.id}`}>{this.state.event.name}</Link>
                </h2>
                <table>
                    <tr>
                        <tc className="event-detail-field">By: </tc>
                        <tc>
                            <Link to={`/orgs/${this.state.org.id}`}>{this.state.org.name}</Link>
                        </tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">On: </tc>
                        <tc>{this.state.event.startTime.toISOString()}</tc>
                    </tr>
                </table>
            </>
        );
    }

    render() {
        if (!this.state) {
            return <div/>;
        }

        if (this.props.preview) {
            return this.preview();
        }

        return (
            <div>
                <h1>
                    <Link to={`/events/${this.state.event.id}`}>{this.state.event.name}</Link>
                </h1>
                <h2>Details</h2>
                <table>
                    <tr>
                        <tc className="event-detail-field">By: </tc>
                        <tc>
                            <Link to={`/orgs/${this.state.org.id}`}>{this.state.org.name}</Link>
                        </tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">On: </tc>
                        <tc>{this.state.event.startTime.toISOString()}</tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">Until: </tc>
                        <tc>{this.state.event.endTime.toISOString()}</tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">At: </tc>
                        <tc>{this.state.event.location}</tc>
                    </tr>
                </table>

                <h2>Description</h2>
                <p>{this.state.event.description}</p>

                <Announcements eventID={this.props.eventID} />
            </div>
        );
    }
}

class Announcements extends Component {
    constructor(props) {
        super(props);
    }

    componentDidMount()  {
        this.setState({
            announcements: [
                {
                    date: new Date(),
                    body: "announcement 1",
                },
                {
                    date: new Date(),
                    body: "announcement 2",
                },
            ],
        });
    }

    render() {
        if (!this.state) {
            return <div/>;
        }

        const announcements = this.state.announcements.map((a) => (
            <li>
                <h3>{a.date.toISOString()}</h3>
                <p>{a.body}</p>
            </li>
        ))

        return (
            <Fragment>
                <h2>Announcements</h2>
                <ol>
                    {announcements}
                </ol>
            </Fragment>
        );
    }
}
