import { Fragment, Component } from "react";
import { Link } from "react-router-dom";

export default class Event extends Component {
    constructor(props) {
        super(props);

        if (props.model) {
            this.state = props.model;
        }
    }

    componentDidMount() {
        if (!this.state) {
            this.setState({
                event: {
                    id: this.props.eventID,
                    name: "event name",
                    orgID: 2,
                    description: "event description",
                    startTime: new Date(),
                    endTime: new Date(),
                    tags: ["gaming", "greek"],
                    location: "price center",
                    created: new Date(),
                    updated: new Date(),
                },
                org: {
                    name: "org name",
                },
                announcements: [
                    {
                        date: new Date(),
                        body: "announcement 1",
                    },
                    {
                        date: new Date(),
                        body: "announcement 2",
                    },
                ],
            });
        }
    }

    preview(className) {
        return (
            <>
                <h2>
                    <Link to={`/events/${this.state.event.id}`}>{this.state.event.name}</Link>
                </h2>
                <table>
                    <tr>
                        <tc className="event-detail-field">By: </tc>
                        <tc>
                            <Link to={`/orgs/${this.state.org.id}`}>{this.state.org.name}</Link>
                        </tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">On: </tc>
                        <tc>{this.state.event.startTime.toISOString()}</tc>
                    </tr>
                </table>
            </>
        );
    }

    edit() {
        const announcements = this.state.announcements.map((a, i) => (
            <Fragment key={i}>
                <h3>{a.date.toISOString()}</h3>
                <p>{a.body}</p>

                <label className="event-edit-field"
                    key={i}
                    for={`announcement-${i}`}>announcement date</label>

                <textarea
                    form="event-edit-form"
                    name={`annoucement-${i}`}>announcement1</textarea>

            </Fragment>
        ));

        return (
            <div>
                <h1>
                    Edit: {this.state.event.name}
                </h1>

                <form id="event-edit-form">
                    <label className="event-edit-field" >Event Name
                        <input name="event-name" type="text" value="EVENT NAME"/>
                    </label>

                    <label className="event-edit-field">Starting at
                        {/*
                            datetime-local has a widget for all mobile browsers but only on
                            chrome for desktop
                        */}
                        <input name="start-time" type="datetime-local" value="YYYY-MM-DD hh:mm"/>
                    </label>

                    <label className="event-edit-field">Until
                        {/*
                            datetime-local has a widget for all mobile browsers but only on
                            chrome for desktop
                        */}
                        <input name="end-time" type="datetime-local" value="YYYY-MM-DD hh:mm"/>
                    </label>

                    <label className="event-edit-field">Located at
                        <input name="end-time" type="text" value="LOCATION"/>
                    </label>

                    <label className="event-edit-field" for="description">Description</label>

                    <textarea
                        form="event-edit-form" name="description"
                        >Here's an event description</textarea>

                    {/*
                        this button is not part of the form, merely a way to interact
                        with the form, therefore it is not an input
                    */}
                    <button className="event-edit-field" type="button">New Announcement</button>

                    {/* one of these per announcement */}

                    {announcements}

                </form>
            </div>
        );
    }

    render() {
        if (!this.state) {
            return <div/>;
        }

        if (this.props.preview) {
            return this.preview();
        }

        if (this.props.edit) {
            return this.edit()
        }

        const announcements = this.state.announcements.map((a, i) => (
            <li key={i}>
                <h3>{a.date.toISOString()}</h3>
                <p>{a.body}</p>
            </li>
        ));

        return (
            <div>
                <h1>
                    {this.state.event.name}
                </h1>
                <h2>Details</h2>
                <table>
                    <tr>
                        <tc className="event-detail-field">By: </tc>
                        <tc>
                            <Link to={`/orgs/${this.state.org.id}`}>{this.state.org.name}</Link>
                        </tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">On: </tc>
                        <tc>{this.state.event.startTime.toISOString()}</tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">Until: </tc>
                        <tc>{this.state.event.endTime.toISOString()}</tc>
                    </tr>
                    <tr>
                        <tc className="event-detail-field">At: </tc>
                        <tc>{this.state.event.location}</tc>
                    </tr>
                </table>

                <h2>Description</h2>
                <p>{this.state.event.description}</p>

                <h2>Announcements</h2>
                <ol>
                    {announcements}
                </ol>
            </div>
        );
    }
}
